import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [isJumping, setIsJumping] = useState(false);
  const [rabbitTop, setRabbitTop] = useState(150);
  const [obstacleLeft, setObstacleLeft] = useState(300);
  const [isGameOver, setIsGameOver] = useState(false);

  // Jump when space pressed
  const handleJump = () => {
    if (isJumping || isGameOver) return;
    setIsJumping(true);
    let jumpUp = setInterval(() => {
      setRabbitTop((prev) => {
        if (prev > 80) return prev - 10;
        clearInterval(jumpUp);
        let fallDown = setInterval(() => {
          setRabbitTop((prev) => {
            if (prev < 150) return prev + 10;
            clearInterval(fallDown);
            setIsJumping(false);
            return 150;
          });
        }, 50);
        return prev;
      });
    }, 50);
  };

  // Move obstacle
  useEffect(() => {
    if (isGameOver) return;
    const obstacleMove = setInterval(() => {
      setObstacleLeft((prev) => {
        if (prev < -20) return 300;
        return prev - 5;
      });
    }, 30);

    return () => clearInterval(obstacleMove);
  }, [isGameOver]);

  // Collision check
  useEffect(() => {
    if (obstacleLeft < 80 && obstacleLeft > 30 && rabbitTop > 120) {
      setIsGameOver(true);
    }
  }, [obstacleLeft, rabbitTop]);

  // Restart game
  const restartGame = () => {
    setIsGameOver(false);
    setRabbitTop(150);
    setObstacleLeft(300);
  };

  // Space key for jump
  useEffect(() => {
    const handleKey = (e) => {
      if (e.code === "Space") handleJump();
    };
    document.addEventListener("keydown", handleKey);
    return () => document.removeEventListener("keydown", handleKey);
  });

  return (
    <div className="game" onClick={handleJump}>
      <h2>üêá Rabbit Jump</h2>
      <div className="game-area">
        <div className="rabbit" style={{ top: rabbitTop + "px" }}></div>
        <div className="obstacle" style={{ left: obstacleLeft + "px" }}></div>
        <div className="ground"></div>
      </div>
      {isGameOver ? (
        <button onClick={restartGame}>Restart Game</button>
      ) : (
        <p>Click or Press Space to Jump</p>
      )}
    </div>
  );
}

export default App;
