import React, { useEffect, useState } from "react";
import "./RabbitGame.css";

import rabbitImg from "../assets/rabbit.png";
import obstacleImg from "../assets/obstacle.png";
import jumpSound from "../assets/jump.mp3";
import gameOverSound from "../assets/gameover.mp3";

const RabbitGame = () => {
  const [isJumping, setIsJumping] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  const jumpAudio = new Audio(jumpSound);
  const overAudio = new Audio(gameOverSound);

  // Jump function
  const jump = () => {
    if (!isJumping && !gameOver) {
      setIsJumping(true);
      jumpAudio.play();
      setTimeout(() => setIsJumping(false), 500);
    }
  };

  // Restart the game
  const restartGame = () => {
    setGameOver(false);
    setScore(0);
    document.querySelector(".obstacle").style.animation = "moveObstacle 2s linear infinite";
  };

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.code === "Space") jump();
    };

    document.addEventListener("keydown", handleKeyDown);

    const obstacle = document.querySelector(".obstacle");
    const rabbit = document.querySelector(".rabbit");

    const checkCollision = setInterval(() => {
      if (rabbit && obstacle && !gameOver) {
        const rabbitRect = rabbit.getBoundingClientRect();
        const obstacleRect = obstacle.getBoundingClientRect();

        if (
          rabbitRect.left < obstacleRect.right &&
          rabbitRect.right > obstacleRect.left &&
          rabbitRect.bottom > obstacleRect.top
        ) {
          overAudio.play();
          setGameOver(true);
          obstacle.style.animation = "none";
        } else {
          setScore((prev) => prev + 1);
        }
      }
    }, 100);

    return () => {
      document.removeEventListener("keydown", handleKeyDown);
      clearInterval(checkCollision);
    };
  }, [gameOver]);

  return (
    <div className="game-container" onClick={jump}>
      <div className="score">Score: {score}</div>
      {gameOver && (
        <>
          <div className="game-over">Game Over!</div>
          <button className="restart-btn" onClick={restartGame}>
            Restart
          </button>
        </>
      )}
      <img
        src={rabbitImg}
        className={`rabbit ${isJumping ? "jump" : ""}`}
        alt="Rabbit"
      />
      <img src={obstacleImg} className="obstacle" alt="Obstacle" />
    </div>
  );
};

export default RabbitGame;
